resources.contents:
{
    "openapi": "3.0.0",
    "info": {
        "title": "GLPI API integration",
        "description": "GLPI API usage example, made for the internal use of Beltis, focused on the report extractions and integrations through API",
        "version": "1.0",
        "contact": {
            "email": "apiteam@swagger.io"
        }
    },
    "tags": [
        {
            "name": "Session",
            "description": "Requests about session initialize end terminating"
        },
        {
            "name": "User",
            "description": "Requests about session initialize end terminating"
        }
    ],
    "servers": [
        {
            "url": "http://glpi.beltis.com.br/glpi/apirest.php",
            "description": "External access to glpi"
        },
        {
            "url": "http://192.168.3.28/glpi/apirest.php",
            "description": "Local access to glpi"
        }
    ],
    "paths": {
        "/initSession": {
            "get": {
                "summary": "Create a session to use the API",
                "tags": [
                    "Session",
                    "User"
                ],
                "parameters": [
                  {
                    "name": "App-Token",
                    "in": "header",
                    "description": "The api token",
                    "required": true,
                    "schema": {
                      "$ref": "#/components/schemas/appToken"
                    },
                    "style": "simple"
                  },
                  {
                    "name": "Authorization",
                    "in": "header",
                    "description": "The user-token to access the api",
                    "required": true,
                    "example": {
                      "Authorization": "user-token wioueqiueqoiuewoqiueqoue"
                    },
                    "schema": {
                      "type": "string",
                      "items": {
                        "format": "string"
                      }                      
                    },
                    "style": "simple"
                  },
                ],
                "responses": {
                    "200": {
                        "description": "Succeed",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/initialize_session_response_succeed"
                                }
                            }
                        }
                    },
                    "400": {
                        "description": "Link not authorized, you must authorize you api to access the API",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/initialize_session_response_unauthorized"
                                }
                            }
                        }
                    }
                }
            }
        }
    },
    "components": {
        "schemas": {
            "appToken": {
                "type": "string",
                "description": "The api token",
                "example": {
                    "App-Token": "lno7nlpds3imacrqhshpunumm0"
                },
                "properties": {
                    "session_token": {
                        "type": "string",
                        "description": "The api token"
                    }
                }
            },
            "initialize_session_response_succeed": {
                "type": "object",
                "example": {
                    "session_token": "lno7nlpds3imacrqhshpunumm0"
                },
                "properties": {
                    "session_token": {
                        "type": "string",
                        "description": "The token for the session initialized",
                        "default": "estes"
                    },
                    "json": {
                        "name": "initialize_session_response_succeedSessionToken"
                    }
                }
            },
            "initialize_session_response_unauthorized": {
                "type": "array",
                "items": {
                    "type": "string",
                    "properties": {}
                },
                "example": [
                  "ERROR_MESSAGE",
                  "Error description"
                ]
            }
        }
    }

}
